partBlock = DomInputValProcessor(oDom);
if (partBlock == &quot;&quot;) {
partBlock = partArray[i];
}
if (oDom.code == &apos;STROKE&apos;) {
formattedStroke = partBlock;
}
} catch (myexcept) {
}
}
if(specList != &apos;&apos;) {
specList = specList + &apos;,(&quot;&apos; + oDom.did + &apos;&quot;,&quot;&apos; + partBlock + &apos;&quot;)&apos;;
