oEMsgStr.addMessage(34701,&apos;Size 30 is available with M5 port type only&apos;);
oEMsgStr.addMessage(34702,&apos;Size 30 requires 90&#xb0; or 100&#xb0; rotating angle&apos;);
oDomainToRuleMap = oDomains.createDomainToRuleMap(aExRules);
